import React, { useEffect, useState } from "react";
import service from "./service.js";
import { Link } from "react-router-dom";
import "./app.css"; // üëà ◊¢◊ô◊¶◊ï◊ë

function App() {
  const [newTodo, setNewTodo] = useState("");
  const [todos, setTodos] = useState([]);
  const completedCount = todos.filter(t => t.isComplete).length;

  async function getTodos() {
    try {
      const tasks = await service.getTasks();
      setTodos(tasks);
    } catch {
      console.error("failed to load todos");
    }
  }

  async function createTodo(e) {
    e.preventDefault();
    if (newTodo.trim() === "") return;
    await service.addTask(newTodo.trim());
    setNewTodo("");
    await getTodos();
  }

  async function updateCompleted(todo, isComplete) {
    try {
      await service.setCompleted(todo.id, isComplete, todo.name);
      await getTodos();
    } catch (err) {
      console.error("update failed", err);
    }
  }

  async function deleteTodo(id) {
    await service.deleteTask(id);
    await getTodos();
  }

  useEffect(() => {
    getTodos();
  }, []);

  return (
    <div className="page" dir="rtl">
      <nav className="topnav">
        <div className="brand">
          <span className="logo">üìù</span>
          <span className="brand-title">TickDone</span>
        </div>
        <nav className="auth-nav">
        <Link to="/" className="nav-link">Todos</Link>
        <span className="sep">‚Ä¢</span>
        <span className="nav-link current">◊î◊™◊ó◊ë◊®◊ï◊™</span>
        <span className="sep">‚Ä¢</span>
        <Link to="/register" className="nav-link">◊î◊®◊©◊û◊î</Link>
      </nav>
      </nav>

      <main className="wrap">
        <section className="card">
          <header className="header">
            <h1 className="title">◊î◊û◊©◊ô◊û◊ï◊™ ◊©◊ú◊ô</h1>
            <div className="stats">
              <span className="pill">◊°◊î◊¥◊õ: {todos.length}</span>
              <span className="pill success">◊ë◊ï◊¶◊¢◊ï: {completedCount}</span>
            </div>
            <form onSubmit={createTodo} className="addrow">
              <input
                className="input"
                placeholder="◊û◊î ◊¶◊®◊ô◊ö ◊ú◊¢◊©◊ï◊™ ◊î◊ô◊ï◊ù?"
                value={newTodo}
                onChange={(e) => setNewTodo(e.target.value)}
              />
              <button className="btn">◊î◊ï◊°◊§◊î</button>
            </form>
          </header>

          {todos.length === 0 ? (
            <div className="empty">
              <div className="empty-emoji">üå§Ô∏è</div>
              <div className="empty-title">◊ê◊ô◊ü ◊û◊©◊ô◊û◊ï◊™ ◊õ◊®◊í◊¢</div>
              <div className="empty-sub">◊õ◊™◊ë◊ô ◊ú◊û◊¢◊ú◊î ◊ï◊î◊ï◊°◊ô◊§◊ô ◊ê◊™ ◊î◊û◊©◊ô◊û◊î ◊î◊®◊ê◊©◊ï◊†◊î ◊©◊ú◊ö</div>
            </div>
          ) : (
            <section className="main">
              <ul className="todo-list">
                {todos.map((todo) => (
                  <li
                    key={todo.id}
                    className={`todo ${todo.isComplete ? "done" : ""}`}
                  >
                    <label className="todo-row">
                      <input
                        className="chk"
                        type="checkbox"
                        checked={todo.isComplete}
                        onChange={(e) => updateCompleted(todo, e.target.checked)}
                      />
                      <span className="todo-name">{todo.name}</span>
                    </label>
                    <button
                      className="del"
                      title="◊û◊ó◊ß"
                      onClick={() => deleteTodo(todo.id)}
                    >
                      ‚úï
                    </button>
                  </li>
                ))}
              </ul>
            </section>
          )}
        </section>

        <footer className="foot">
          <span>◊†◊ï◊¶◊® ◊ë◊ê◊î◊ë◊î ‚Ä¢ ◊ê◊†◊ô◊û◊¶◊ô◊ï◊™ ◊¢◊ì◊ô◊†◊ï◊™ ‚Ä¢ RTL ◊û◊ú◊ê</span>
        </footer>
      </main>
    </div>
  );
}

export default App;
